SELECT count(*)
    FROM pg_stat_activity
    WHERE query NOT ILIKE '%pg_stat_activity%' and datname NOT ILIKE '%azure_sys%' and application_name != '';

SELECT datname as Database, pid, application_name, state, query, backend_type
    FROM pg_stat_activity
    WHERE query NOT ILIKE '%pg_stat_activity%' and datname NOT ILIKE '%azure_sys%' and application_name != '';

SELECT state, COUNT(*) FROM pg_stat_activity  
WHERE pid <> pg_backend_pid() AND query NOT ILIKE '%pg_stat_activity%' and datname NOT ILIKE '%azure_sys%' and application_name != ''
GROUP BY 1;

SELECT application_name, COUNT(*) FROM pg_stat_activity  
WHERE pid <> pg_backend_pid() and query NOT ILIKE '%pg_stat_activity%' and datname NOT ILIKE '%azure_sys%' and application_name != ''
GROUP BY 1 ORDER BY 1;
